import React, { useState } from 'react'
import Create from './pages/Create'
import ContentList from './pages/ContentList'
import dummyDates from './resource/dummyData';
import './App.css'

function App() {
  const [data, setData] = useState(dummyDates);

  const msgCard = ({msgCard}) => {
    const newData = [msgCard, ...data];
    setData(newData); //뭔가 이쯤에서 뭔일이 있어나는듯 -ㅅ-;;; 담아오는 거 다시 확인해보기..
    console.log('클릭되나?');
  }

  const msgDataList = data.map(data => {
    return(<ContentList msgList={data} key={data.id}/>)
  })


  return (
    <div className='container'>
      {/* 왼쪽 목록 */}
      <div className="table con-panal">
        <div className='table-title'>
          <span>목록</span>
          <button>글쓰기</button>
        </div>
        {msgDataList}
      </div>
      {/* 디폴트 상태 - 가장 최근 글 */}
      <div className='con-panal'>
        <h3>제목</h3>
        <div>내용</div>
      </div>

      {/* 글쓰기 */}
      <Create onCreate={msgCard}/>
    </div>
  )
}

export default App
