  //   onCreate({addDis}); //재배열
  // }

  const handleButtonCancleClick = () => {
    setMode('WELCOME');
  }
  return (
    <div className='CreateContent table'>
      <span class="lb">SUBJECT</span>
      <input 
      className='ipt-form' 
      placeholder='제목 입력'
      onChange={handleChangeSubject}
      ></input>
      <span class="lb">CONTENT</span>
      <textarea 
      className='ipt-form' 
      placeholder='내용 입력'
      onChange={handleChangeMsg}
      ></textarea>
      <div class="btn-group">
        <button className="btn-sm sub" onClick={handleButtonCancleClick}>취소</button>
        <button className="btn-sm" onClick={addDis}><FontAwesomeIcon icon={faCheck} /> 저장</button>
      </div>
    </div>
  )
}

export default Create
