import React, { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCheck } from '@fortawesome/free-solid-svg-icons';
import './Create.css'

function Create({dataList, setMode, countId, countIdF}) {
  console.log(countId);
  const [subject, setSubject] = useState('');
  const [msg, setMsg] = useState('');

  const handleChangeSubject = (event) => {
    setSubject(event.target.value);
  };

  const handleChangeMsg = (event) => {
    return setMsg(event.target.value);
  };



  const handleButtonClick = () => {
    const addData = {

      id:12,
      username:'woong',
      title:'너무힘들어',
      content: '다신안해 더미데이터 수정',
      createdAt: new Date().toLocaleString()
      // id:countId,
      // username: 'gest',
      // title:subject,
      // content: msg,
      // createdAt: new Date().toLocaleString()
    };
    fetch(`http://localhost:3001/discussions/`, { 
      method: 'POST', 
      body : JSON.stringify(addData)
    })
    .then((res) => {
      if (res.status === 201) {
        dataList()
      }
    })

    countIdF();
    setMode('WELCOME');
    // onCreate({addDis}); //재배열
  }

  const handleButtonCancleClick = () => {
    setMode('WELCOME');
  }
  return (
    <div className='CreateContent table'>
      <span class="lb">SUBJECT</span>
      <input 
      className='ipt-form' 
      placeholder='제목 입력'
      onChange={handleChangeSubject}
      ></input>
      <span class="lb">CONTENT</span>
      <textarea 
      className='ipt-form' 
      placeholder='내용 입력'
      onChange={handleChangeMsg}
      ></textarea>
      <div class="btn-group">
        <button className="btn-sm sub" onClick={handleButtonCancleClick}>취소</button>
        <button className="btn-sm" onClick={handleButtonClick}><FontAwesomeIcon icon={faCheck} /> 저장</button>
      </div>
    </div>
  )
}

export default Create
