import React from 'react'
import './ContentList.css'
import dummyDates from '../resource/dummyData';

function ContentList(list = []) {
  if (list.length === 0) {
    return <div className="merge-col">목록이 없습니다</div>;
  }

  return (
    list.map(data => {
      return(
        <div className="lst-card row">
          <div>
            {/* data의 번호 */}
            <span className="lst-num">{data.id}</span>
            {/* data의 제목 */}
            <span className="lst-subject">{data.title}</span>
          </div>
          {/* data의 작성일자 */}
          <div className="lst-data">{new Date(data.createdAt).toLocaleDateString('ko-kr')}</div>
        </div>
      )
    })
  )
}

export default ContentList
