import React, { useState } from 'react'

function Update({data, setMode, onUpdate}){
    const [title, setTitle] = useState(data[0].title);
    const [msg, setMsg] = useState(data[0].content);

    function changeSubject(event){
        setTitle(console.log(event.target.value));
    }

    function changeContent(event){
        setMsg(console.log(event.target.value));
    }

    const handleButtonClick = () => {
        const msgCard = {
          id: data.length+1,
          username: 'guest',
          title: title,
          content:msg,
          createdAt: new Date(),
        }
        // setLstNum(lstNum=>lstNum+1);
        setMode('WELCOME');
        onUpdate({msgCard}); //재배열
      }

    return (
        <div className='CreateContent con-panal'>
          <input 
          className='ipt-form' 
          placeholder='제목 입력'
          value={title}
          onChange={changeSubject}
          ></input>
          <textarea 
          className='ipt-form' 
          placeholder='내용 입력'
          value={msg}
          onChange={changeContent}
          ></textarea>
          <div>
            <button >취소</button>
            <button onClick={handleButtonClick}>수정완료</button>
          </div>
        </div>
      )
  }


export default Update
