import React, { useState } from 'react'

function Update({data}){
    const [title, setTitle] = userState(data[0].title);
    const [msg, setMsg] = userState(data[0].content);

    function changeSubject(event){
        setTitle(console.log(event.target.value));
    }

    function changeContent(event){
        setMsg(console.log(event.target.value));
    }

    return (
        <div className='CreateContent con-panal'>
          <input 
          className='ipt-form' 
          placeholder='제목 입력'
          value={title}
          onChange={changeSubject}
          ></input>
          <textarea 
          className='ipt-form' 
          placeholder='내용 입력'
          value={msg}
          onChange={changeContent}
          ></textarea>
          <div>
            <button>취소</button>
            <button>수정완료</button>
          </div>
        </div>
      )
  }


export default Update
