import React, { useState } from 'react'
import Create from './pages/Create'
import ContentList from './pages/ContentList'
import PrintContent from './pages/PrintContent'
import dummyDates from './resource/dummyData';
import './App.css'

function App() {
  const [data, setData] = useState(dummyDates);
  const [conSubject, setConSubject] = useState('');
  const [conDate, setConDate] = useState('');
  const [conMsg, setConMsg] = useState('');

  const msgCard = ({msgCard}) => {
    const newData = [msgCard, ...data];
    setData(newData); //뭔가 이쯤에서 뭔일이 있어나는듯 -ㅅ-;;; 담아오는 거 다시 확인해보기..
    console.log('클릭되나?');
  }

  const msgDataList = data.map(data => {
    return(<ContentList msgList={data} key={data.id} clickCardHandler={msgPrint}/>)
  })


  function msgPrint(event) {
    //모드가 내용이 보이는걸로 바뀌고

    //누른거 아이디 값 가져오고 새로 담긔
    const filterId = event.target.id;
    const sltData = data.filter(data => {return(data.id === Number(filterId))});
    setConSubject (sltData[0].title);
    setConDate (sltData[0].createdAt);
    setConMsg (sltData[0].content);
    console.log(conSubject, conDate, ConMsg);
  }

  return (
    <div className='container'>
      {/* 왼쪽 목록 */}
      <div className="table con-panal">
        <div className='table-title'>
          <span>목록</span>
          <button>글쓰기</button>
        </div>
        {msgDataList}
      </div>
      {/* 디폴트 상태 - 가장 최근 글 */}
      <PrintContent onPrint={conSubject, conDate, conMsg}/>

      {/* 글쓰기 */}
      <Create onCreate={msgCard}/>
    </div>
  )
}

export default App
