import React, { useState } from 'react'
import './Create.css'

function Create({onCreate, setMode}) {
  const [subject, setSubject] = useState('');
  const [msg, setMsg] = useState('');
  const [lstNum, setLstNum] = useState(lstNumber);
  let lstNumber = 4;
  const handleChangeSubject = (event) => {
    setSubject(event.target.value);
  };

  const handleChangeMsg = (event) => {
    return setMsg(event.target.value);
  };

  const handleButtonClick = () => {
    const msgCard = {
      id: lstNumber,
      username: 'guest',
      title: subject,
      content:msg,
      createdAt: new Date(),
    }
    setLstNum(lstNumber+1);
    setMode('WELCOME');
    onCreate({msgCard}); //재배열
  }

  const handleButtonCancleClick = () => {
    setMode('WELCOME');
  }
  return (
    <div className='CreateContent con-panal'>
      <input 
      className='ipt-form' 
      placeholder='제목 입력'
      onChange={handleChangeSubject}
      ></input>
      <textarea 
      className='ipt-form' 
      placeholder='내용 입력'
      onChange={handleChangeMsg}
      ></textarea>
      <div>
        <button onClick={handleButtonCancleClick}>취소</button>
        <button onClick={handleButtonClick}>저장</button>
      </div>
    </div>
  )
}

export default Create
